import React, { useState } from "react";
import { ButtonGroup, Button } from "react-bootstrap";

function EquityCalculator(){
    const [numPlayers, setNumPlayers] = useState(2);

    return(
        <div className="eq-calc-container">
            <ButtonGroup>
                <Button variant="secondary" disabled onClick={() => setNumPlayers(numPlayers - 1)}>-</Button>
                <Button className="btn-disabled" variant="secondary">{numPlayers} Player(s)</Button>
                <Button variant="secondary" onClick={() => setNumPlayers(numPlayers + 1)}>+</Button>
            </ButtonGroup>
        </div>
    )
}

export default EquityCalculator;
