import React, { useState } from "react";
import { Row, Col, Modal, Button } from "react-bootstrap";
import CardBack from "../assets/images/deck/z_cardback.png";

function Board(){
    const [show, setShow] = useState(false);

    const cards = [
        ["A", "K", "Q", "J", "T", "9", "8", "7", "6", "5", "4", "3", "2"],
        ["A", "K", "Q", "J", "T", "9", "8", "7", "6", "5", "4", "3", "2"],
        ["A", "K", "Q", "J", "T", "9", "8", "7", "6", "5", "4", "3", "2"],
        ["A", "K", "Q", "J", "T", "9", "8", "7", "6", "5", "4", "3", "2"]
    ];

    const cardSelector = Array.from(Array(4), _ => Array(13).fill(false));

    const cardsSelected = [];

    return(
        <div>
            <div className="row board"onClick={() => setShow(true)}>
                <img className="board-playing-card" alt="card" src={CardBack}/>
                <img className="board-playing-card" alt="card" src={CardBack}/>
                <img className="board-playing-card" alt="card" src={CardBack}/>
                <img className="board-playing-card" alt="card" src={CardBack}/>
                <img className="board-playing-card" alt="card" src={CardBack}/>
            </div>

            <Modal centered show={show} onHide={() => setShow(false)}>
                <Modal.Header >
                    <Modal.Title>Board Card Selector</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                {cards.map((item, i) => { return (
                    <Row className="flex-nowrap">
                    {item.map((subitem, j) => { return (
                        <Col className={"board-modal-card " + (i % 2 ? "red" : "black")}>
                            <div>{subitem}</div>
                            {(() => {
                                switch(i){
                                    case 0:
                                        return <i className="bi-suit-spade-fill"></i>;
                                    case 1:
                                        return <i className="bi-suit-heart-fill"></i>;
                                    case 2:
                                        return <i className="bi-suit-club-fill"></i>;
                                    case 3:
                                        return <i className="bi-suit-diamond-fill"></i>;
                                    default:
                                        return <i/>;
                                }
                            })()}
                        </Col>
                    )})}
                    </Row>
                )})}
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={() => setShow(false)}>Submit</Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}

export default Board;
