import { ButtonGroup, Button } from "react-bootstrap";

const games = ["Online Cash", "MTT"];

    const onlineCashOptions = [
      ["RFI", "VS RFI", "RFI VS 3-Bet", " VS 4-Bet"],
      ["UTG", "HJ", "CO", "BTN", "SB", "BB"],
      ["UTG", "HJ", "CO", "BTN", "SB", "BB"]
    ];

    let onlineCashSelections = ["", "", "", ""];

    let randomizer = "--";

    onClickButtonGroup = (option, param) => {
        onlineCashSelections[option] = param;
        // if(option == 1 && param == "RFI"){
        //   option++;
        //   onlineCashSelections[option] = "None";
        // }

        // for(let i = option+1; i < onlineCashSelections.length; i++){
        //   onlineCashSelections[i] = "";
        // }
    }

function PreflopCharts(){


    return(
        <div className="container">
            <div className="btn-group-spacing">
            {games.map((item) => { return ( 
                <Button className="btn-spacing" variant={(onlineCashSelections === item ? "light" : "secondary")} onClick={() => onClickButtonGroup(0, item)}>{item}</Button>
            )})}
            </div>
            {onlineCashOptions.map((item) => { return (
            <div className="btn-group-spacing">
                <ButtonGroup size="sm">
                {item.map((subitem) => { return (
                    <Button variant="secondary">{subitem}</Button>
                )})}
                </ButtonGroup>
            </div>
            )})}
        </div>
    )
}

export default PreflopCharts;
