import React, { useState } from "react";
import { ButtonGroup, Button } from "react-bootstrap";

function EquityCalculator(){
    const [players, setPlayers] = useState([true, true]);

    const addPlayer = () => {
        const newPlayers = [...players];
        newPlayers.push(true);
        console.log(newPlayers)
        setPlayers(newPlayers);
    }

    const removePlayer = () => {
        const newPlayers = [...players];
        newPlayers.pop();
        setPlayers(newPlayers);
    }

    const removePlayerAtIndex = (index) => {
        const newPlayers = [...players];
        newPlayers.splice(index, 1);
        setPlayers(newPlayers);
    }

    return(
        <div className="eq-calc-container">
            <div>
                <ButtonGroup style={{margin: "1em"}}>
                    <Button variant="secondary" disabled={players.length <= 2} obClick={() => removePlayer()}>-</Button>
                    <Button className="btn-disabled" variant="secondary">{players.length} Players</Button>
                    <Button variant="secondary" disabled={players.length >= 9} onClick={() => addPlayer()}>+</Button>
                </ButtonGroup>
            </div>

            <div style={{margin: "1em"}}>
            {players.map((player, index) => { return (
                <div key={index}>
                    <ButtonGroup style={{margin: "0.5em", width: "100%"}}>
                        <div className="player-num">P{index+1}</div>
                        <Button variant="outline-danger" onClick={() => removePlayerAtIndex(index)}>&times;</Button>
                        <Button variant="secondary">Hand</Button>
                        <Button variant="secondary">Range</Button>
                        <Button className="btn-disabled range-display" variant="light">Empty</Button>
                        <Button variant="secondary">Win</Button>
                        <Button variant="secondary">Tie</Button>
                    </ButtonGroup>
                </div>
            )})}

            </div>    
        </div>
    )
}

export default EquityCalculator;
