import React, { useState } from "react";
import { ButtonGroup, Button } from "react-bootstrap";

function EquityCalculator(){
    const [numPlayers, setNumPlayers] = useState(2);

    return(
        <div className="eq-calc-container">
            <div>
                <ButtonGroup style={{margin: "1em"}}>
                    <Button variant="secondary" disabled={numPlayers <= 2} onClick={() => setNumPlayers(numPlayers - 1)}>-</Button>
                    <Button className="btn-disabled" variant="secondary">{numPlayers} Players</Button>
                    <Button variant="secondary" disabled={numPlayers >= 9} onClick={() => setNumPlayers(numPlayers + 1)}>+</Button>
                </ButtonGroup>
            </div>

            <div style={{margin: "1em"}}>
            {numPlayers.map((i) => { return(
                <div key={i}>
                    <ButtonGroup style={{margin: "0.5em"}}>
                        <Button variant="outline-danger">&times;</Button>
                        <Button variant="secondary">Hand</Button>
                        <Button variant="secondary">Range</Button>
                        <Button className="btn-disabled range-display" variant="light">Empty</Button>
                        <Button variant="secondary">Win</Button>
                        <Button variant="secondary">Tie</Button>
                    </ButtonGroup>
                </div>
            )})}

            </div>    
        </div>
    )
}

export default EquityCalculator;
