import React, { useState } from "react";
import { ButtonGroup, Button } from "react-bootstrap";

function EquityCalculator(){
    const [numPlayers, setNumPlayers] = useState(2);

    return(
        <div className="eq-calc-container">
            <ButtonGroup>
                <Button variant="secondary" disabled={numPlayers <= 2} onClick={() => setNumPlayers(numPlayers - 1)}>-</Button>
                <Button className="btn-disabled" variant="secondary">{numPlayers} Players</Button>
                <Button variant="secondary" disabled={numPlayers >= 9} onClick={() => setNumPlayers(numPlayers + 1)}>+</Button>
            </ButtonGroup>

            <ButtonGroup>
                <Button>&times;</Button>
                <Button>Hand</Button>
                <Button>Range</Button>
                <Button>Empty</Button>
                <Button>Win</Button>
                <Button>Tie</Button>
            </ButtonGroup>
        </div>
    )
}

export default EquityCalculator;
