import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./styles.css";

import NavigationBar from "./components/NavigationBar";
import Home from "./pages/Home";
import PreflopCharts from "./pages/PreflopCharts";

function App() {
  return (
    <div className={(window.location.pathname === "/" || window.location.pathname === "/home" ? "background" : "no-background")}>
      <NavigationBar/>
      <Router>
        <Switch>
          <Route path="/home">
            <Home />
          </Route>
          <Route path="/preflop-charts">
            <PreflopCharts />
          </Route>
          <Route path="/equity-calculator">
            <h1>Equity Calculator</h1>
          </Route>
          <Route path="/">
            <h1>Home</h1>
          </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
