import React, { useState } from "react";
import { Modal } from "react-bootstrap";
import CardBack from "../assets/images/deck/z_cardback.png";

function Board(){
    const [show, setShow] = useState(false);

    return(
        <div>
            <div className="row board" onClick={() => setShow(true)}>
                <img className="board-playing-card" alt="card" src={CardBack}/>
                <img className="board-playing-card" alt="card" src={CardBack}/>
                <img className="board-playing-card" alt="card" src={CardBack}/>
                <div style={{width: "2vw"}}/>
                <img className="board-playing-card" alt="card" src={CardBack}/>
                <div style={{width: "2vw"}}/>
                <img className="board-playing-card" alt="card" src={CardBack}/>
            </div>

            <Modal show={show} onHide={() => setShow(false)}>

            </Modal>
        </div>
    )
}

export default Board;
